import {useState} from 'react';

function Square({ value, onSquareClick }) {
  // const [value, setValue] = useState(null);


  return(
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}

// State ÎÅåÏñ¥Ïò¨Î¶¨Í∏∞Î∂ÄÌÑ∞!

function Board({ xIsNext, squares, onPlay, boardSize, playerNames }) {
  // const [xIsNext, setXIsNext] = useState(true);
  // const [squares, setSquares] = useState(Array(9).fill(null));

  function handleClick(i) {
    if (squares[i] || calculateWinner(squares, boardSize)) {
      return;
    }
    const nextSquares = squares.slice();
    if (xIsNext) {
      nextSquares[i] = "X";
    } else {
      nextSquares[i] = "O";
    }
    onPlay(nextSquares);
    // setSquares(nextSquares);
    // setXIsNext(!xIsNext);
  }

  const winner = calculateWinner(squares, boardSize);
  let status;
  if (winner) {
    const winnerName = winner === "X" ? playerNames.playerX : playerNames.playerO;
    status = `${winnerName}ÎãòÏù¥ ÏäπÎ¶¨ÌïòÏÖ®ÏäµÎãàÎã§! üéâ`;
  } else {
    const currentPlayer = xIsNext ? playerNames.playerX : playerNames.playerO;
    status = `Îã§Ïùå ÌîåÎ†àÏù¥Ïñ¥: ${currentPlayer} (${xIsNext ? "X" : "O"})`;
  }

  return (
    <div>
  <div className="status">{status}</div>
  <div className="board">
    {Array.from({length: boardSize}, (_, row) => (
      <div key={row} className="board-row">
        {Array.from({length: boardSize}, (_, col) => {
          const index = row * boardSize + col;
          return (
            <Square 
              key={index}
              value={squares[index]} 
              onSquareClick={() => handleClick(index)} 
            />
          );
        })}
      </div>
    ))}
  </div>
</div>
  );
}

export default function Game() {
  const [xIsNext, setXIsNext] = useState(true);
  const [boardSize, setBoardSize] = useState(5);
  const [history, setHistory] = useState([Array(25).fill(null)]);
  const [playerNames, setPlayerNames] = useState({ playerX: "ÌîåÎ†àÏù¥Ïñ¥ 1", playerO: "ÌîåÎ†àÏù¥Ïñ¥ 2" });
  const [showNameInput, setShowNameInput] = useState(false);
  const currentSquares = history[history.length - 1];

   function handleNameChange(player, newName) {
    setPlayerNames(prev => ({
      ...prev,
      [player]: newName
    }));
  }

  // üî• ÏÉàÎ°ú Ï∂îÍ∞Ä - Ïù¥Î¶Ñ ÏûÖÎ†• ÌÜ†Í∏Ä Ìï®Ïàò
  function toggleNameInput() {
    setShowNameInput(!showNameInput);
  }

  function changeGameMode(newSize) {
    setBoardSize(newSize);
    setHistory([Array(newSize * newSize).fill(null)]);
    setXIsNext(true);
  }

  function handlePlay(nextSquares) {
    setHistory([...history, nextSquares]);
    setXIsNext(!xIsNext);
  }

  function jumpTo(nextMove) {
    //todo
  }

  const moves = history.map((squares, move) => {
    let description;
    if (move > 0) {
      description = 'Go to move #' + move;
    } else {
      description = 'Go to game start';
    }
    return (
      <li key={move}>
        <button onClick={() => jumpTo(move)}>{description}</button>
      </li>
    );
  });

  return (
    <div className="game">
      <div className="player-settings section">
        <h3>ÌîåÎ†àÏù¥Ïñ¥ ÏÑ§Ï†ï</h3>
        <button className="btn" onClick={toggleNameInput}>
          {showNameInput ? 'Ïù¥Î¶Ñ ÏÑ§Ï†ï ÏôÑÎ£å' : 'ÌîåÎ†àÏù¥Ïñ¥ Ïù¥Î¶Ñ Î≥ÄÍ≤Ω'}
        </button>

        {showNameInput && (
          <div className="name-inputs">
            <div>
              <label>ÌîåÎ†àÏù¥Ïñ¥ 1 (X): </label>
              <input 
                type="text" 
                value={playerNames.playerX}
                onChange={(e) => handleNameChange('playerX', e.target.value)}
                placeholder="ÌîåÎ†àÏù¥Ïñ¥ 1 Ïù¥Î¶Ñ"
              />
            </div>
            <div>
              <label>ÌîåÎ†àÏù¥Ïñ¥ 2 (O): </label>
              <input 
                type="text" 
                value={playerNames.playerO}
                onChange={(e) => handleNameChange('playerO', e.target.value)}
                placeholder="ÌîåÎ†àÏù¥Ïñ¥ 2 Ïù¥Î¶Ñ"
              />
            </div>
          </div>
        )}
      <div className="game-mode-selector">
        <h3>Í≤åÏûÑ Î™®Îìú ÏÑ†ÌÉù:</h3>
        <button 
          className={`button ${boardSize === 5 ? 'active' : ''}`} 
          onClick={() => changeGameMode(5)}
        >
          Ïò§Î™© (5x5)
        </button>
        <button 
          className={`button ${boardSize === 7 ? 'active' : ''}`} 
          onClick={() => changeGameMode(7)}
        >
          Ïπ†Î™© (7x7)
        </button>
        <button 
          className={`button ${boardSize === 10 ? 'active' : ''}`}  
          onClick={() => changeGameMode(10)}
        >
          Ïã≠Î™© (10x10)
        </button>
      </div>

      </div>

      <div className="game-mode-selector">
        <h3>Í≤åÏûÑ Î™®Îìú ÏÑ†ÌÉù:</h3>
        <div className="button-group">
          <button className={boardSize === 5 ? 'active' : ''} onClick={() => changeGameMode(5)}>
            Ïò§Î™© (5x5)
          </button>
          <button className={boardSize === 7 ? 'active' : ''} onClick={() => changeGameMode(7)}>
            Ïπ†Î™© (7x7)
          </button>
          <button className={boardSize === 10 ? 'active' : ''} onClick={() => changeGameMode(10)}>
            Ïã≠Î™© (10x10)
          </button>
        </div>
      </div>



      <div className="section game-board">
        <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} boardSize={boardSize} playerNames={playerNames} />
      </div>
      <div className="section game-info">
        <ol>{moves}</ol>
      </div>
    </div>
  );
}

function calculateWinner(squares, boardSize) {
  const lines = [];

  // Í∞ÄÎ°úÏ§Ñ
  for (let i = 0; i < boardSize; i++) {
    const row = [];
    for (let j = 0; j < boardSize; j++) {
      row.push(i * boardSize + j);
    }
    lines.push(row);
  }

  // ÏÑ∏Î°úÏ§Ñ
  for (let j = 0; j < boardSize; j++) {
    const col = [];
    for (let i = 0; i < boardSize; i++) {
      col.push(i * boardSize + j);
    }
    lines.push(col);
  }

  // ÎåÄÍ∞ÅÏÑ†
  const diagonal1 = [];
  const diagonal2 = [];
  for (let i = 0; i < boardSize; i++) {
    diagonal1.push(i * boardSize + i);           // ÏôºÏ™Ω ÏúÑ ‚Üí Ïò§Î•∏Ï™Ω ÏïÑÎûò
    diagonal2.push(i * boardSize + (boardSize - 1 - i)); // Ïò§Î•∏Ï™Ω ÏúÑ ‚Üí ÏôºÏ™Ω ÏïÑÎûò
  }
  lines.push(diagonal1, diagonal2);


  // for (let i = 0; i < lines.length; i++ ) {
  //   const [a, b, c, d, e] = lines[i];
  //   if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c] && squares[a] === squares[d] && squares[a] === squares[e]) {
  //     return squares[a];
  //   }
  // }
  for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const firstSquare = squares[line[0]];

  if (firstSquare && line.every(index => squares[index] === firstSquare)) {
    return firstSquare;
  }

}
  return null;
}
